import React, { useState } from 'react';
import axios from 'axios';

const AddCarForm = () => {
  const [newCar, setNewCar] = useState({
    make: '',
    model: '',
    year: '',
    color: '',
  });

  const handleChange = (e) => {
    setNewCar({ ...newCar, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/cars', newCar)
      .then((res) => {
        console.log(res.data);
        setNewCar({
          make: '',
          model: '',
          year: '',
          color: '',
        });
      })
      .catch((err) => {
        console.error(err);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="make">Make:</label>
        <input type="text" id="make" name="make" value={newCar.make} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="model">Model:</label>
        <input type="text" id="model" name="model" value={newCar.model} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="year">Year:</label>
        <input type="text" id="year" name="year" value={newCar.year} onChange={handleChange} />
      </div>
      <div>
        <label htmlFor="color">Color:</label>
        <input type="text" id="color" name="color" value={newCar.color} onChange={handleChange} />
      </div>
      <button type="submit">Add Car</button>
    </form>
  );
};

export default AddCarForm;
