import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CarList() {
  const [cars, setCars] = useState([]);

  useEffect(() => {
    axios
      .get('http://localhost:5000/cars')
      .then((response) => {
        setCars(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  const handleDelete = (id) => {
    axios
      .delete(`http://localhost:5000/cars/${id}`)
      .then((response) => {
        setCars(cars.filter((car) => car.id !== id));
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div className="bg-red rounded shadow p-4 center carlist">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-red text-3xl font-bold">Car List</h1>
        <nav className="bg-blue-900 text-white p-4 flex justify-between items-center">
      <div className="text-2xl font-bold">Car Management</div>
      <div className="space-x-4">
        <a href="#" className="hover:text-gray-400">Add Car</a>
        <a href="#" className="hover:text-gray-400">Update Car</a>
        <a href="#" className="hover:text-gray-400">Delete Car</a>
      </div>
    </nav>
      </div>
      <ul>
        {cars.map((car) => (
          <li key={car.id} className="flex items-center justify-between">
            <div className="text-lg font-medium">{car.make} {car.model}</div>
            <div className="text-gray-500">{car.year}</div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CarList;
