import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "../App.css"

function CarList() {
    const [cars, setCars] = useState([]);

    useEffect(() => {
      axios
        .get('http://localhost:5000/cars')
        .then((response) => {
          setCars(response.data);
        })
        .catch((error) => {
          console.error(error);
        });
    }, []);
    const handleDelete = (id) => {
        axios
          .delete(`http://localhost:5000/cars/${id}`)
          .then((response) => {
            setCars(cars.filter((car) => car.id !== id));
          })
          .catch((error) => {
            console.error(error);
          });
      };

    return (
        <div className="bg-red rounded shadow p-4 center carlist">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-red text-3xl font-bold">Car List</h1>
          <div>
            <a href="/add-car" className="text-gray-500 mr-4">
              Add Car
            </a>
            <a href="/update-car" className="text-gray-500 mr-4">
              Update Car
            </a>
            <a href="/delete-car" className="text-gray-500" onClick={() => handleDelete(cars[0].id)}>
              Delete Car
            </a>
          </div>
        </div>

        <h1 className="text-3xl font-bold mb-4">Car List</h1>
        <ul>
          {cars.map((car) => (
            <li key={car.id} className="flex items-center justify-between">
              <div className="text-lg font-medium">{car.make} {car.model}</div>
              <div className="text-gray-500">{car.year}</div>
            </li>
          ))}
        </ul>
      </div>
    );
  }

  export default CarList;
